import React, {useState, useEffect} from 'react'
import { Country, State, City }  from 'country-state-city';
import Header from '../../Extras/Header'
import Admin_sidebar from '../../Extras/Admin_sidebar'
import './admin_style.css'
import { getStatesOfCountry } from 'country-state-city/lib/state';

function Admin_Home() {

  const countries = require('country-state-city').Country;
  const states = require('country-state-city').State;


   const [country, setCountry] = useState([]);
   const [state, setState] = useState([]);
  
  useEffect(()=>{
     setCountry(countries.getAllCountries());
   });
  
const handleCountry = (name, index) =>{

  console.log(index);
  console.log(name);
}

  return (
    <>
      <Admin_sidebar />
      <Header />
      <div className="card">
     <select className='form-control' onChange={(e) => handleCountry(e.target.value.index, e.target.index)} >
      <option value="8">Select Country</option>
      {
        country && country !== undefined ?
        country.map((ctr, index) => {
          return(
            <option key={index} value={ctr.id}>{ctr.name}
            </option>
          )
        })
        :''
      }
     </select>
     <br></br>
     <select className='form-control'>
      <option value="8">Select State</option>
      {
        state && state !== undefined ?
        state.map((ctr, index) => {
          return(
            <option key={index} value={ctr.id}>{ctr.name}</option>
          )
        })
        :''
      }
     </select>
     
      <div className="card-body">
          
          <h2 style={{'textAlign':'center'}}>MEET THE TEAM</h2>
        <div className="card-group"  id='ABOUT' style={{'marginLeft':'-25%', 'marginTop':'-3%'}}>
      <div className="card">
        <img className="card-img-top" src={require("../../Extras/huzaifa.jpg")} alt="Card image cap"></img>
        <div className="card-body">
          <h5 className="card-title">HUZAIFA AKA CUTIE</h5>
          <p className="card-text">TEXT</p>
        </div>
        {/* <div className="card-footer">
          <small className="text-muted">Last updated 3 mins ago</small>
        </div> */}
      </div>
      <div className="card">
        <img className="card-img-top" src={require("../../Extras/maarij.jpg")} alt="Card image cap"></img>
        <div className="card-body">
          <h5 className="card-title">MAARIJ AMIR AKA SHAPATAR</h5>
          <p className="card-text">TEXT</p>
        </div>
      {/* <div className="card-footer">
        <small className="text-muted">Last updated 3 mins ago</small>
      </div> */}
    </div>
    <div className="card">
      <img className="card-img-top" src={require("../../Extras/rayan.jpg")} alt="Card image cap"></img>
      <div className="card-body">
        <h5 className="card-title">RAYAN AKA DATA SCIENTIST</h5>
        <p className="card-text">TEXT</p>
      </div>
      {/* <div className="card-footer">
        <small className="text-muted"></small>
      </div> */}
    </div>
    </div>
        </div>
      </div>

    </>
  )
}

export default Admin_Home
