import React, {useEffect} from 'react'
import { useFormik } from "formik";
import Select from "react-select";
import { Country, State, City} from "country-state-city";
import Header from '../../Extras/Header'
import Admin_sidebar from '../../Extras/Admin_sidebar'
import './admin_style.css'

function Admin_Home() {
  const addressFromik = useFormik({
    initialValues: {
      country: "Pakistan",
      state: null,
      city: null
    },
    onSubmit: (values) => console.log(JSON.stringify(values))
  });

  const countries = Country.getAllCountries();

  const updatedCountries = countries.map((country) => ({
    label: country.name,
    value: country.id,
    ...country
  }));
  const updatedStates = (countryId) =>
    State
      .getStatesOfCountry(countryId)
      .map((state) => ({ label: state.name, value: state.id, ...state }));
  const updatedCities = (stateId) =>
    City.getCitiesOfState(stateId).map((city) => ({ label: city.name, value: city.id, ...city }));

  const { values, handleSubmit, setFieldValue, setValues } = addressFromik;

  useEffect(() => {}, [values]);



  return (
    <>
      <Admin_sidebar />
      <Header />
      <div classNameName="card">
      <form onSubmit={handleSubmit}>
        <Select
          id="country"
          name="country"
          label="country"
          options={updatedCountries}
          value={values.country}
          onChange={(value) => {
            setValues({ country: value, state: null, city: null }, false);
          }}
        />
        <Select
          id="state"
          name="state"
          options={updatedStates(values.country ? values.country.value : null)}
          value={values.state}
          onChange={(value) => {
            setValues({ state: value, city: null }, false);
          }}
        />
        <Select
          id="city"
          name="city"
          options={updatedCities(values.state ? values.state.value : null)}
          value={values.city}
          onChange={(value) => setFieldValue("city", value)}
        />
        <button type="submit">Submit</button>
        {JSON.stringify(Country.get)}
        {JSON.stringify(State.get)}
        {JSON.stringify(City.get)}
      </form>        






        <div className="card-body">
          <h2 style={{'textAlign':'center'}}>MEET THE TEAM</h2>
        <div class="card-group"  id='ABOUT' style={{'marginLeft':'-25%', 'marginTop':'-3%'}}>
      <div class="card">
        <img class="card-img-top" src={require("../../Extras/huzaifa.jpg")} alt="Card image cap"></img>
        <div class="card-body">
          <h5 class="card-title">HUZAIFA AKA CUTIE</h5>
          <p class="card-text">TEXT</p>
        </div>
        {/* <div class="card-footer">
          <small class="text-muted">Last updated 3 mins ago</small>
        </div> */}
      </div>
      <div class="card">
        <img class="card-img-top" src={require("../../Extras/maarij.jpg")} alt="Card image cap"></img>
        <div class="card-body">
          <h5 class="card-title">MAARIJ AMIR AKA SHAPATAR</h5>
          <p class="card-text">TEXT</p>
        </div>
      {/* <div class="card-footer">
        <small class="text-muted">Last updated 3 mins ago</small>
      </div> */}
    </div>
    <div class="card">
      <img class="card-img-top" src={require("../../Extras/rayan.jpg")} alt="Card image cap"></img>
      <div class="card-body">
        <h5 class="card-title">RAYAN AKA DATA SCIENTIST</h5>
        <p class="card-text">TEXT</p>
      </div>
      {/* <div class="card-footer">
        <small class="text-muted"></small>
      </div> */}
    </div>
    </div>
        </div>
      </div>

    </>
  )
}

export default Admin_Home
